
public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		byte my_age = 22;
		short my_salary = 10000;
		int pincode = 827013;
		long my_phone = 9857464557L;
		
		float percentage = 92.78f;
		double annualIncome = 76448789.7547874d;
		
		boolean isIndian = true;
		char section = 'B';
		
		String my_name = "Nisha Soni 756859 $&#;^*%";
		
		System.out.println("byte : "+my_age);
		System.out.println("short : "+my_salary);
		System.out.println("int : "+pincode);
		System.out.println("long : "+my_phone);
		System.out.println("float : "+percentage);
		System.out.println("double : "+annualIncome);
		System.out.println("boolean : "+isIndian);
		System.out.println("char : "+section);
		System.out.println("String : "+my_name);
		
	}

}
